let lines = clonedChild.textContent?.split("\n");
lines?.map((line, index) => {
  let newSpan = document.createElement("span");
  newSpan.className = className;
  newSpan.textContent = line;

  // Add a line break after each span, except the last one
  if (index < lines!.length - 1) {
    newSpan.textContent += "\n";
  }
  result.push(newSpan);
}

const constructStyles = (codeBlock: SyntaxHighlightConfig) => {
  const styles: ISyntaxStyleProps = {
    ...defaultHostConfig.codeBlock,
    backgroundColor:
      codeBlock.backgroundColor ??
      defaultHostConfig.codeBlock.backgroundColor,
    borderColor:
      codeBlock.borderColor ?? defaultHostConfig.codeBlock.borderColor,
    syntaxColors:
      (codeBlock.syntaxColors as SyntaxColorSetDefinition) ??
      defaultHostConfig.codeBlock.syntaxColors,
  };
  return styles;
};

