export default class CodeBlock extends BaseCodeBlock {
  protected internalRender(): JSX.Element | null {
  // Then register the languages you need
  hljs.registerLanguage("javascript", javascript);

  return this.codeSnippet ? (
    <div>
      <h5>{this.fileName}</h5>
      <p>Lines {this.startLineNumber} - {this.endLineNumber}</p>
      <HighlightCodeBlockWrapper
        language={this.language ?? "plaintext"}
        offset={this.startLineNumber}
      >
        {this.codeSnippet}
      </HighlightCodeBlockWrapper>
    </div>
  ) : (
    <div>NOT SUPPORTED</div>
    );
  }
}
